graphql.query:
query Query {
  getAllCities{
    id,
    name,
    square
  }
  }

query GetDescriptions($city1Id: ID!, $city2Id: ID!, $city3Id: ID!, $city4Id: ID! ) {
  city1: getCityDescription(id: $city1Id)
  city2: getCityDescription(id: $city2Id)
  city3: getCityDescription(id: $city3Id)
  city4: getCityDescription(id: $city4Id)
}

query GetSomeCities($min: Int!, $max: Int!) {
    getCityByRangeId(filter: {minId: $min, maxId: $max}){
    id
    name
    square
      ...on City {
    description
  }
    }
}

mutation NewCity($NewName: String!, $NewSquare: Float!, $NewDescription: String!  )  {
    addCity(name:$NewName, square:$NewSquare, description:$NewDescription ){
      id
    name
    square
    ...on City{
        description
    }
    }
  }
